import React from "react";
import Item from "./components/item";

import "./App.css";

function App() {
  const [authors, setAuthors] = React.useState([]);
  const [value, setValue] = React.useState("");

  React.useEffect(() => {
    fetch("http://localhost:3001/authors")
      .then(res => res.json())
      .then(result => {
        setAuthors(result);
      });
  }, []);

  const handleEdit = (id, newValue) => {
    const data = {
      name: newValue
    };

    fetch(`http://localhost:3001/authors/${id}`, {
      method: "PATCH",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(data)
    })
      .then(res => res.json())
      .then(result => {
        setAuthors(
          authors.map(author => {
            if (author.id !== id) return author;
            return result;
          })
        );
      });
  };

  const handleSubmit = event => {
    event.preventDefault();

    if (value && value !== "") {
      const data = {
        name: value
      };
      fetch("http://localhost:3001/authors", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(data)
      })
        .then(res => res.json())
        .then(result => {
          console.log(result);
          setAuthors(authors.concat([result]));
          setValue("");
        });
    }
  };

  const handleDelete = id => {
    fetch(`http://localhost:3001/authors/${id}`, {
      method: "DELETE"
    })
      .then(res => res.json())
      .then(result => {
        setAuthors(authors.filter(author => author.id !== id));
      });
  };

  return (
    <div className="App">
      <h1>Favorite Books</h1>
      <p>Keep track of your favorites!</p>
      <ul>
        {authors.map(book => (
          <Item
            key={book.id}
            value={book.name}
            handleEdit={newValue => handleEdit(book.id, newValue)}
            handleDelete={() => handleDelete(book.id)}
          />
        ))}
      </ul>

      <form onSubmit={handleSubmit} className="new-form">
        <input
          className="new-input"
          value={value}
          onChange={event => setValue(event.target.value)}
          placeholder="eg. The Great Gatsby"
        />
        <button className="button primary-button" type="submit">
          Submit
        </button>
      </form>
    </div>
  );
}

export default App;
