import React from "react";
import Item from "./components/item";

import "./App.css";

function App() {
  const [authors, setAuthors] = React.useState([]);
  const [value, setValue] = React.useState("");

  React.useEffect(() => {
    fetch("http://localhost:3001/authors")
      .then(res => res.json())
      .then(result => {
        setAuthors(result);
      });
  }, []);

  const handleEdit = (id, newValue) => {
    const data = {
      name: value
    };
    fetch(`http://localhost:3001/authors/${id}`, {
      method: "UPDATE",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(data)
    })
      .then(res => res.json())
      .then(result => {
        setAuthors(authors.filter(author => author.id !== id));
      });
  };

  const handleSubmit = event => {
    event.preventDefault();

    if (value && value !== "") {
      const data = {
        name: value
      };
      fetch("http://localhost:3001/authors", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(data)
      })
        .then(res => res.json())
        .then(result => {
          console.log(result);
          setAuthors(authors.concat([result]));
        });
    }
  };

  const handleDelete = id => {
    fetch(`http://localhost:3001/authors/${id}`, {
      method: "DELETE"
    })
      .then(res => res.json())
      .then(result => {
        setAuthors(authors.filter(author => author.id !== id));
      });
  };

  return (
    <div className="App">
      <header className="App-header">
        <ul>
          {authors.map(author => (
            <Item
              key={author.id}
              value={author.name}
              handleEdit={newValue => handleEdit(author.id, newValue)}
            />
          ))}
        </ul>

        <form onSubmit={handleSubmit}>
          <input
            value={value}
            onChange={event => setValue(event.target.value)}
            placeholder="input author name"
          />
          <button type="submit">Submit</button>
        </form>
      </header>
    </div>
  );
}

export default App;
