import React from "react";

const Item = props => {
  const [value, setValue] = React.useState(props.value);
  const [editMode, setEditMode] = React.useState(false);

  const handleEdit = () => props.handleEdit(value);

  console.log("isInEditMode");

  if (editMode)
    return (
      <div>
        <input
          value={value}
          onChange={event => setValue(event.target.value)}
          type="text"
        />
        <button onClick={handleEdit}>okay</button>
      </div>
    );

  return (
    <div>
      {editMode && (
        <React.Fragment>
          <input
            value={value}
            onChange={event => setValue(event.target.value)}
            type="text"
          />
          <button onClick={handleEdit}>okay</button>
        </React.Fragment>
      )}
    </div>
  );
};

export default Item;
